package com.open.proxy.aop;/** * 被代理对象元数据 *  * @author jinming.wu * @date 2014-4-7 */public class TargetSource {    private Object     target;    private Class<?>   targetClazz;    private Class<?>[] interfaces;    public TargetSource(Object target, Class<?> targetClazz, Class<?>... interfaces) {        this.target = target;        this.targetClazz = targetClazz;        this.interfaces = interfaces;    }    public Object getTarget() {        return target;    }    public void setTarget(Object target) {        this.target = target;    }    public Class<?> getTargetClazz() {        return targetClazz;    }    public void setTargetClazz(Class<?> targetClazz) {        this.targetClazz = targetClazz;    }    public Class<?>[] getInterfaces() {        return interfaces;    }    public void setInterfaces(Class<?>[] interfaces) {        this.interfaces = interfaces;    }}